<template>
    <div id="top-panel">
        <profilePhoto class="miniProfile" :image="profilePic"
                      :username="username" />
        <dropDownMenu class="dropDownMenu" :users="users"
                      @submit-query="userSearch" />
        <!--submit-query event emitted from dropDownMenu element is caught and the userSearch function is triggered
        ; the information sent with the submit-query event is passed to the triggered function as an argument
        -->
    </div>
</template>

<script>
    import profilePhoto from "../components/profilePhoto";
    import dropDownMenu from "../components/dropDownMenu";
    // importing necessary components

    export default {
        name: "HomePage",
        components: {
            profilePhoto,
            dropDownMenu,
        },
        // defining components used
        data() {
            return {
                username: "",
                profilePic: "",
                users: [],
                mockDatabase: [{ username: "Bob", photo: "data:image/jpeg;base64," }
                    , { username: "Beagle", photo: "data:image/jpeg;base64," },
                    { username: "Freddo", photo: "data:image/jpeg;base64," }],
            }
        },
        // component props are associated with vue data stored here and manipulated by methods (use props instead?)
        methods: {
            async getProfilePic() {
                try {
                    await fetch("http://localhost:3000/api/getProfilePhoto", {
                        method: "GET",
                        headers: { "Authorization": document.cookie.slice(6) },
                    })
                        .then(res => {

                            if (res.status === 401) {
                                alert("user authentication failed");
                                window.location.href = "http://localhost:8080/";
                            }
                            // if authentication fails then alert message and user is redirected back to the login page

                            return res.json()
                            // otherwise extract response json data
                        })
                        .then(data => {
                            this.username = data.username;
                            this.profilePic = data.photo;
                        })
                        .catch(() => alert("unable to retrieve user data"))
                        // change data based on json data

                } catch (err) {
                    alert(err);
                }
            }
            // makes fetch request and sets username and profilePic data based on response
            ,
            userSearch(query) {
                const regex = new RegExp(`^${query.length > 0 ? query : null}`, "i");
                this.users = this.mockDatabase.filter((userObject) => regex.test(userObject.username));
            },
            // queries user profile data based on user search 
        },
        beforeMount() {
            this.getProfilePic();
        },
        // call functions on page load
    }

</script>

<style>
    #top-panel {
        position: relative;
        border-bottom: solid;
        top: 150px;
    }

    .dropDownMenu {
        position: absolute;
        left: 550px;
        top: -175px;
    }

    .miniProfile {
        position: absolute;
        top: -200px;
        left: 1500px;
    }
</style>